    <!-- Body and community if there is a URL or media -->
    <div v-if="post.content.body.length">
      <div class="pl-1">
        <div class="text-sm inline-flex items-center">
          <img v-if="post.community.icon.length && preferences.showPostCommunityIcon" :src="post.community.icon"
            class="h-4 w-4 rounded-sm mt-0.5 mr-1">
          <p class="text-sm flex-grow-0 text-gray-300">{{ post.community.name }}</p>
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="5"
            stroke-linecap="round" stroke-linejoin="round" class="w-4 h-4 text-gray-400">
            <path d="M9 18l6-6-6-6" />
          </svg>
          <p class="text-sm flex-grow-0 text-gray-400">{{ post.community.actor_domain }}</p>
        </div>
      </div>
      <!-- Post creator -->
      <div class="pl-1">
        <div class="inline-flex items-center">
          <!-- Custom avatars enabled and avatar image present -->
          <div class="avatar mr-0.5"
