            <div class="w-4 rounded-full mr-0.5">
              <img :src="post.creator.avatar" :alt="`${post.creator.name}@${post.creator.actor_domain}`"
                :title="`${post.creator.name}@${post.creator.actor_domain}`" />
            </div>
          </div>
